import React, { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import AuthService from "../../services/authservice";
import Usercontex from "../Store/Usercontex";
import "./Login.css";


export const Login = () => {
  const navigate = useNavigate();
  const { setUserData } = useContext(Usercontex);
console.log
  const [user, setUser] = useState({
    email: "",
    password: "",
  });
  const [successful, setSuccessful] = useState(false);
  const [message, setMessage] = useState("");

  const validationLoginHandler = () => {
    if (!user.email || !user.password) {
      setSuccessful(false);
      setMessage("All fields are required!");
      return false;
    }
    if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(user.email)) {
      setSuccessful(false);
      setMessage("Please enter valid email.");
      return false;
    }
    if (user.password.length < 8 || user.password.length > 40) {
      setSuccessful(false);
      setMessage("The password must be between 8 and 40 characters.");
      return false;
    }
    return true;
  };

  const onChangeHandler = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const loginSubmitHandler = (e) => {
    e.preventDefault();
    var formIsValid = validationLoginHandler();
    if (formIsValid) {
      AuthService.login(user).then((response) => {   
        console.log(response.sessUser.token);
        setUserData({
          isLoggedIn:true,
          token:response.sessUser.token,
