import React, { useState, useContext } from 'react'
import { Link, Navigate, Redirect, useNavigate } from 'react-router-dom';
import "./Register.css";
import UserRegisterContex from '../Store/UserRegisterContex';

export const Login = () => {
    const navigate = useNavigate();
    const dataCtx = useContext(UserRegisterContex);
    const[error,setError]=useState("")
    const[states,setstates]=useState({
      email: "",
      password:"",
    })
    console.log(states);
    const valueChangeHandler = (e) => {
        setstates({
          ...states,
          [e.target.name]: e.target.value,
        });
        console.log();
      };


    const handleSubmit = (e) => {
        e.preventDefault();
        const user = dataCtx.registerdata.find(
          (u) => u.email === states.email
        );
        console.log(user);
        if (user !== undefined) {
          if (user.password === states.password) {
            dataCtx.login(user)  
            navigate('/Homepage')
      
          }else{
            setError("Invalid Password")
          }
        }else{
          setError("Invalid Credentials")
        }
    }
      
  return (
    <section className="vh-100 bg-image" style={{backgroundImage: 'url("https://mdbcdn.b-cdn.net/img/Photos/new-templates/search-box/img4.webp")'}}>
    <div className="mask d-flex align-items-center h-100 gradient-custom-3">
      <div className="container h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-12 col-md-9 col-lg-7 col-xl-6">
            <div className="card" style={{borderRadius: '15px'}}>
              <div className="card-body p-5">
                <h2 className="text-uppercase text-center mb-5">Login Account</h2>
                <form >
                  <div className="form-outline mb-4">
                    <input type="email"  className="form-control form-control-lg" name="email" value={states.email} onChange={valueChangeHandler} />
                    <label className="form-label" >Your Email</label>
                  </div>
                  <div className="form-outline mb-4">
                    <input type="password"  className="form-control form-control-lg" name="password" value={states.password} onChange={valueChangeHandler}/>
                    <label className="form-label">Password</label>
                  </div>
                  <div className="d-flex justify-content-center">
                    <button type="submit" className="btn btn-success btn-block btn-lg gradient-custom-4 text-body" onClick={handleSubmit}>Login</button>
                  </div>
                  <span>{error}</span>
                  <p className="text-center text-muted mt-5 mb-0">For Create New Account <a  className="fw-bold text-body"><u><Link to="/Register">Register Here</Link></u></a></p>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
  )
}
