import React , { useReducer } from 'react'
import UserRegisterContex from './UserRegisterContex';


const registerdata = [
    {
      id: "1",
      name: "khushal",
      email: "kk@gmail.com",
      password:"1234",
    },
    {
      id: "2",
      name: "khush",
      email: "kk1@gmail.com",
      password:"1235",
    },
  ];

  var initialValue = {
    registerdata: registerdata,
  };

  const todoReducer = (state, action) => {
    switch (action.type) {
      case "ADD":
        return {
          ...state,
          dummydata: state.dummydata.concat(action.item),
          updatedData: state.dummydata.concat(action.item),
         
        };
      case "DELETE":
        const deleteUserData = state.updatedData.filter(
          (data1) => data1.id !== action.itemId
        );
  
        return {
          ...state,
          updatedData :deleteUserData,
          dummydata: deleteUserData,
        };
      case "SEARCH":
     
        const filterItems = state.dummydata.filter((item) => {
          return (
            item.value.toLowerCase().indexOf(action.itemInput.toLowerCase()) > -1
          ); // true
        });
        return {
          ...state,
          updatedData: state.dummydata,
          dummydata: action.itemInput.length ? filterItems : state.dummydata,
        };
      case "CHECKED":
        const updatedCheckedData = state.dummydata.map((item) => {
          return item.id === action.itemchecked
            ? { ...item, isChecked: !item.isChecked }
            : item;
        });
  
        return {
          ...state,
          updatedData: updatedCheckedData,
          dummydata: updatedCheckedData,
        };
  
      case "CHANGESTATE":
        var dataArray = state.updatedData;
        if (action.itemcheckedCase === "all") {
          dataArray = state.updatedData;
        } else if (action.itemcheckedCase === "completed") {
          dataArray = state.updatedData.filter((item) => {
            return item.isChecked === true;
          });
        } else if (action.itemcheckedCase === "active") {
          dataArray = state.updatedData.filter((item) => {
            return item.isChecked === false;
          });
        }
        else if(action.itemcheckedCase != action.type){
          dataArray = state.dummydata;
        }
        return {
           ...state,
          updatedData: state.updatedData,
          dummydata: dataArray,
        };
      case "THEME":
        if( state.darkMode === false){
        return { 
          ...state,
          darkMode:true 
        };}
        else{
          return { 
            ...state,
            darkMode:false 
          };
        }
    }
  
    return initialValue;
  };

export const UserRegisterProvider = (props) => {
