import React, { useState, useContext } from "react";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";
import "./Register.css";
import AuthService from "../../services/authservice";

export const Register = () => {
  const navigate = useNavigate();
  const [enteredValue, setEnteredValue] = useState({
    name: "",
    email: "",
    password: "",
  });
  const [successful, setSuccessful] = useState(false);
  const [message, setMessage] = useState("");

  const validationHandler = () => {
    if (!user.username || !user.email || !user.password) {
      setSuccessful(false);
      setMessage('This field is required!');
      return false;
    }
    if (user.username.length < 3 || user.username.length > 20) {
      setSuccessful(false);
      // form.current.username.style.border = '1px solid #f9d7da';
      setMessage("The username must be between 3 and 20 characters.");
      return false;
    }
    if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(user.email)){
      setSuccessful(false);
      setMessage("Please enter valid email.");
      return false;
    }
    if (user.password.length < 6 || user.password.length > 40) {
      setSuccessful(false);
      setMessage("The password must be between 6 and 40 characters.");
      return false;
    }
    if(user.password !== user.confirmpassword){
      setSuccessful(false);
      setMessage("password and confirm password must be same.");
      return false;
    }
    return true;
  }

  const submitHandler = (e) => {
    e.preventDefault();
    // dataCtx.addItem(enteredValue);
    if (enteredValue.name && enteredValue.email && enteredValue.password) {
      AuthService.register(enteredValue).then((response) => {
        console.log(response);
        // setEnteredValue({...enteredValue, [e.target.name] : ''})
        setEnteredValue({
          name: "",
          email: "",
          password: "",
        });
        navigate("/Login");
      });
    }
    // dispatch({
    //   type: "ADD",
    //   payload: enteredValue,
    // });
  };
  const valueChangeHandler = (e) => {
    setEnteredValue({
      ...enteredValue,
      id: Math.random().toString(),
      [e.target.name]: e.target.value,
    });
  };
  return (
    <section
      className="vh-100 bg-image"
      style={{
        backgroundImage:
          'url("https://mdbcdn.b-cdn.net/img/Photos/new-templates/search-box/img4.webp")',
      }}
    >
      <div className="mask d-flex align-items-center h-100 gradient-custom-3">
        <div className="container h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-9 col-lg-7 col-xl-6">
              <div className="card" style={{ borderRadius: "15px" }}>
                <div className="card-body p-5">
                  <h2 className="text-uppercase text-center mb-5">
                    Create an account
                  </h2>
                  <form onSubmit={submitHandler}>
                    {message && (
                      <div className="form-group">
                        <div
                          className={
                            successful
                              ? "alert alert-success"
                              : "alert alert-danger"
                          }
                          role="alert"
                        >
                          {message}
                        </div>
                      </div>
                    )}
                    <div className="form-outline mb-4">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        name="name"
                        value={enteredValue.name}
                        onChange={valueChangeHandler}
                      />
                      <label className="form-label">Your Name</label>
                    </div>
                    <div className="form-outline mb-4">
                      <input
                        type="email"
                        className="form-control form-control-lg"
                        name="email"
                        value={enteredValue.email}
                        onChange={valueChangeHandler}
                      />
                      <label className="form-label">Your Email</label>
                    </div>
                    <div className="form-outline mb-4">
                      <input
                        type="password"
                        className="form-control form-control-lg"
                        name="password"
                        value={enteredValue.password}
                        onChange={valueChangeHandler}
                      />
                      <label className="form-label">Password</label>
                    </div>
                    <div className="d-flex justify-content-center">
                      <button
                        type="submit"
                        className="btn btn-success btn-block btn-lg gradient-custom-4 text-body"
                      >
                        Register
                      </button>
                    </div>
                    <p className="text-center text-muted mt-5 mb-0">
                      Have already an account?{" "}
                      <a className="fw-bold text-body">
                        <u>
                          <Link to="/Login">Login here</Link>
                        </u>
                      </a>
                    </p>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
