import React, { useReducer } from "react";
import UserRegisterContex from "./UserRegisterContex";

const registerdata = [
  {
    id: "1",
    name: "khushal",
    email: "kk@gmail.com",
    password: "1234",
  },
  {
    id: "2",
    name: "khush",
    email: "kk1@gmail.com",
    password: "1235",
  },
];

var initialValue = {
  registerdata: registerdata,
};

const userReducer = (state, action) => {
  switch (action.type) {
    case "ADD":
      return {
        ...state,
        registerdata: state.registerdata.concat(action.item),
      };
    case "LOGIN":
      const user = state.registerdata.find(
        (u) => u.email === action.loginItem.email
      );
      console.log(user);
      if (user !== undefined) {
        if (user.password === action.loginItem.password) {
          console.log(true);
        }
      }
      // console.log(action.loginItem.email);
      return {};
  }

  return initialValue;
};

export const UserRegisterProvider = (props) => {
  const addItemHandler = (itemData) => {
    dispatch({ type: "ADD", item: itemData });
  };
  const loginHandler = (loginData) => {
    // console.log(loginData);
    dispatch({ type: "LOGIN", loginItem: loginData });
  };

  const [state, dispatch] = useReducer(userReducer, initialValue);
  console.log(state);
  const userregistercontex = {
    registerdata: state.registerdata,
    addItem: addItemHandler,
    login: loginHandler,
  };
  return (
    <UserRegisterContex.Provider value={userregistercontex}>
      {props.children}
    </UserRegisterContex.Provider>
  );
};
