import axios from "axios";
import { useState } from "react";
import { Navigate } from "react-router";


const register = (user) => {
  const { name, email, password } = { ...user };
  return axios.post("http://localhost:8081/Register", {
    name,
    email,
    password,
  });
};

//
const login = async (user) => {
  const { email, password } = { ...user };
  return axios
    .post(
      "http://localhost:8081/login",
      {
        email,
        password,
      },
      { withCredentials: true }
    )
    .then((response) => {
      if (response.data.sessUser) {
        console.log(response.data);
        localStorage.setItem("user", JSON.stringify(response.data.sessUser));
      }
      return response.data;
    });
};

//
const logout = async () => {
  
  return axios.post("http://localhost:8081/logout",{},{ withCredentials: true}).then((response) => {
    localStorage.removeItem("user");
    return response.data;
  });
};

//


const checkLoggedIn = async () => {
  let token = localStorage.getItem("user");
  if(token === ""){
    localStorage.setItem("user", "");
    token = "";
  //  return <Navigate to ="/Login"/>
  }

  const tokenResponse = await axios.post('http://localhost:8081/tokenIsValid', null, {headers: {"x-auth-token": token}});
  if (tokenResponse.data) {
    const userRes = await axios.get("http://localhost:8081/", {
      headers: { "user": token },
    });
    // setUserData({
    //   token,
    //   user: userRes.data,
    // });
  }
}

const AuthService = {
  register,
  login,
  logout,
  checkLoggedIn,
};

export default AuthService;
