        //   alert(data.msg);
        // //   navigate("/Login");
        // } catch (err) {
        //   console.log(err);
        //   alert('OTP verification failed.');
        // }
      };
  return (
    <>
       <form onSubmit={verifyOTP}>
      <label>OTP:</label>
      <input type="text" name="otp" value={otp} onChange={(e) => setOtp(e.target.value)} />
      <button type="submit">Verify OTP</button>
    </form>
    </>
  );
};
